#!/usr/bin/env bash

function installNpmDependencies {
    # Recommended approach to reach the internal NPM registry
    # https://hello.atlassian.net/wiki/spaces/BB/pages/193206480/HOW+TO+interact+with+the+Atlassian+private+NPM+registry+in+Bitbucket+Pipelines
    curl -u ${PAC_LOGIN}:${PAC_TOKEN} https://packages.atlassian.com/api/npm/atlassian-npm/auth/atlassian > ~/.npmrc
    npm install
}
