'use strict';

module.exports = (condition, timeout) => new Promise((resolve, reject) => {
    let done = false;

    setTimeout(() => {
        done = true;
        reject(new Error('waitForCondition timeout exceeded'));
    }, timeout);

    const checkCondition = () => {
        const result = condition();

        if (result) {
            resolve();
        } else if (!done) {
            setTimeout(checkCondition, 10);
        }
    };

    checkCondition();
});
